import React, { useState } from "react";
import "./Components/LoginPage/styles.css";
import Login from "./Components/LoginPage/Login";
import SignUp from "./Components/SignupPage/signUp";
import './Components/MainPage/styles.css';

const PlacementTrackerApp = ({ onLoginClick }) => {
    return (
        <div className="container">
            <h1>Placement Tracker App</h1>
            <p>Track opportunities and manage applications with ease</p>
            <div className="buttons">
                <button className="login-button" onClick={() => onLoginClick('student')}>Student Login</button>
                <button className="login-button" onClick={() => onLoginClick('admin')}>Placement Admin Login</button>
            </div>
        </div>
    );
};
export default function App() {
  const [authMode, setAuthMode] = useState(null); // Null means no auth mode selected

  const handleOnClick = mode => {
    setAuthMode(mode);
  };

  const containerClass = "container " + (authMode ? "right-panel-active" : "");

  return (
    <div className="App">
      <h2>Sign in/up Form</h2>
      {authMode === null ? (
        <PlacementTrackerApp onLoginClick={handleOnClick} />
      ) : (
        <div className={containerClass} id="container">
          <SignUp/>
          <Login />
          <div className="overlay-container">
            <div className="overlay">
              <div className="overlay-panel overlay-left">
                <h1>Welcome Back!</h1>
                <p>
                  To keep connected with us please login with your personal info
                </p>
                <button className="ghost" id="signIn" onClick={() => setAuthMode('signIn')}>
                  Sign In
                </button>
              </div>
              <div className="overlay-panel overlay-right">
                <h1>Hello, Friend!</h1>
                <p>Enter your personal details and start journey with us</p>
                <button className="ghost" id="signUp" onClick={() => setAuthMode('signUp')}>
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
