import React, { useState } from "react";
import "./Components/LoginPage/styles.css";
import "./Components/MainPage/styles.css";
import SignInForm from "./Components/LoginPage/Login";
import SignUpForm from "./Components/SignupPage/signUp";
import Dashboard from "./Components/Dashboard/Dashboard"; // Import the new component

const HomePage = ({ onLoginClick }) => {
  return (
    <div className="container landing-container">
      <h1>Placement Tracker App</h1>
      <p>Track opportunities and manage applications with ease</p>
      <div className="buttons">
        <button
          className="login-button"
          onClick={() => onLoginClick("studentLogin")}
        >
          Student Login
        </button>
        <button
          className="login-button"
          onClick={() => onLoginClick("adminLogin")}
        >
          Placement Admin Login
        </button>
      </div>
    </div>
  );
};

export default function App() {
  const [view, setView] = useState("landing");

  // Function to handle sign-in success
  const handleSignInSuccess = () => {
    setView("dashboard");
  };

  // Function to handle sign-out
  const handleSignOut = () => {
    setView("landing");
  };

  const containerClass = "container " + (view === "signUp" ? "right-panel-active" : "");

  return (
    <div className="App">
      {view === "landing" ? (
        <HomePage onLoginClick={setView} />
      ) : view === "dashboard" ? (
        <Dashboard onSignOut={handleSignOut} />
      ) : (
        <div className={containerClass} id="container">
          <SignUpForm />
          {/* Pass the handleSignInSuccess function to the SignInForm */}
          <SignInForm onSignInSuccess={handleSignInSuccess} />
          
          {view === "adminLogin" ? (
            <div className="overlay-container">
              <div className="overlay">
                <div className="overlay-panel overlay-right">
                  <h1>Welcome, Team!</h1>
                  <p>Please log in with your credentials.</p>
                </div>
              </div>
            </div>
          ) : (
            <div className="overlay-container">
              <div className="overlay">
                <div className="overlay-panel overlay-left">
                  <h1>Welcome Back!</h1>
                  <p>To keep connected with the Placement team</p>
                  <button
                    className="ghost"
                    id="signIn"
                    onClick={() => setView("studentLogin")}
                  >
                    Sign In
                  </button>
                </div>
                <div className="overlay-panel overlay-right">
                  <h1>Hello, Friend!</h1>
                  <p>To join with The Rajalakshmi's Placement team</p>
                  <button
                    className="ghost"
                    id="signUp"
                    onClick={() => setView("signUp")}
                  >
                    Sign Up
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
