import React, { useState } from "react";
import "./Components/LoginPage/styles.css";
import "./Components/MainPage/styles.css";
import SignInForm from "./Components/LoginPage/Login";
import SignUpForm from "./Components/SignupPage/signUp";


const HomePage = ({ onLoginClick }) => {
  return (
    <div className="container landing-container">
      <h1>Placement Tracker App</h1>
      <p>Track opportunities and manage applications with ease</p>
      <div className="buttons">
        <button
          className="login-button"
          onClick={() => onLoginClick("signIn")}
        >
          Student Login
        </button>
        <button
          className="login-button"
          onClick={() => onLoginClick("signIn")}
        >
          Placement Admin Login
        </button>
      </div>
    </div>
  );
};

export default function App() {
  const [view, setView] = useState("landing"); // 'landing', 'signIn', 'signUp'
  
  const containerClass =
    "container " + (view === "signUp" ? "right-panel-active" : "");

  return (
    <div className="App">
      {view === "landing" ? (
        <HomePage onLoginClick={setView} />
      ) : (
        <div className={containerClass} id="container">
          <SignUpForm />
          <SignInForm />
          <div className="overlay-container">
            <div className="overlay">
              <div className="overlay-panel overlay-left">
                <h1>Welcome Back!</h1>
                <p>
                  To keep connected with us please login with your personal info
                </p>
                <button
                  className="ghost"
                  id="signIn"
                  onClick={() => setView("signIn")}
                >
                  Sign In
                </button>
              </div>
              <div className="overlay-panel overlay-right">
                <h1>Hello, Friend!</h1>
                <p>Enter your personal details and start journey with us</p>
                <button
                  className="ghost"
                  id="signUp"
                  onClick={() => setView("signUp")}
                >
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
